import { ButtonHTMLAttributes } from "react";
import { IconName } from "../Icon/Icon";
import { Margins } from "../styleguide";
import { PrimaryButtonColors } from "./primaryButtonColors";
export type ButtonVariants = "primary" | "secondary" | "tertiary";
export type ButtonSizes = "Small" | "Medium" | "Large";
type ButtonAttributes = ButtonHTMLAttributes<HTMLButtonElement>;
export interface ButtonBase extends ButtonAttributes {
    dataQA: string;
}
export interface StyledButtonProps extends Margins {
    variant: ButtonVariants;
    size?: ButtonSizes;
    /**
     * @default midBlue
     * @description only applies to primary variant
     * @see Button.tsx
     */
    primaryButtonColor?: PrimaryButtonColors;
}
export interface ButtonProps extends StyledButtonProps, ButtonBase {
    buttonText?: string | number;
    rightIcon?: IconName;
    leftIcon?: IconName;
}
export type LinkButtonProps = Omit<ButtonProps, "size" | "variant" | "primaryButtonColor">;
export interface IconButtonProps extends StyledButtonProps, ButtonBase {
    icon: IconName;
}
export {};
