import { HTMLAttributes, KeyboardEvent, MouseEvent } from "react";
import { Margins } from "../styleguide";
export interface StyledCardProps extends Margins {
    cursor?: string;
    isClickable?: boolean;
    isVisible?: boolean;
    isInverted?: boolean;
    padding?: string;
}
export interface CardProps extends StyledCardProps, HTMLAttributes<HTMLDivElement> {
    dataQA: string;
    ariaLabel: string;
    className?: string;
    children?: React.ReactNode | JSX.Element[];
    onClick?: (event: MouseEvent<HTMLDivElement> | KeyboardEvent<HTMLDivElement>) => void;
}
export interface VerticalCardProps extends Margins {
    dataQA: string;
    ariaLabel: string;
    className?: string;
    cards: CardProps[];
}
export type HorizontalCardProps = VerticalCardProps;
