import { Ref } from "react";
import { GroupBase, Props, SelectInstance } from "react-select";
import { IconName } from "../Icon/Icon";
import { Margins } from "../styleguide";
export type ComboboxValidation = "valid" | "invalid";
export type OptionType = {
    value: string | number | null;
    label: string | null;
    leftIcon?: IconName;
};
export type StyledComboboxProp = {
    validation?: ComboboxValidation;
};
export interface ComboboxProps<Option = OptionType, IsMulti extends boolean = false, Group extends GroupBase<Option> = GroupBase<Option>> extends StyledComboboxProp, Margins, Props<Option, IsMulti, Group> {
    dataQA: string;
    placeholder?: string;
    label?: string;
    labelHtmlFor?: string;
    id?: string;
    helperText?: string;
    comboboxIcon?: IconName;
    comboboxLeftIcon?: IconName;
    comboboxRightIcon?: IconName;
    comboboxRef?: Ref<SelectInstance<Option, IsMulti, Group>>;
}
