import { Props as DayzedProps } from "dayzed";
import { Margins } from "../styleguide";
export type WeekdayMap = {
    /**
     * @description full weekday name used by screen readers
     * @example Sunday, Monday, etc.
     */
    label: string;
    /**
     * @description weekday name shorthand/abbreviation
     * @example Su, Mo, etc.
     */
    value: string;
};
export interface StaticDatePickerProps extends Margins {
    dataQA: string;
    monthNames?: string[];
    weekdayNames?: WeekdayMap[];
    /**
     * @description props passed to the useDayzed hook
     * @see https://github.com/deseretdigital/dayzed#props
     * @default
     * minDate -5 years
     * maxDate +5 years
     */
    dayzedProps: DayzedProps;
    isRange?: boolean | false;
    className?: string;
}
/**
 * @description used to generate custom shortucts while also using the default ones
 * @example
    const shortcuts: Shortcut[] = [
        defaultShortcutsList.today,
        {
            label: "Last 8 days",
            getDates: () => [subDays(today, 8), today],
            id: "last-eight-days",
        },
    ];
*/
export type Shortcut = {
    label: string;
    getDates: () => Date[];
    /** @description `id` is used to show the selected shortcut */
    id: string;
};
