import { CSSProperties } from "react";
import { BaseModalProps } from "../Modal";
export type DrawerSizes = "small" | "medium" | "large" | "fullScreen";
/**
 * @description expand with more positions in case they are needed in the future
 * @example drawerPosition[position]
 */
export type DrawerPositions = "right";
export type DrawerStylesProps = {
    position?: DrawerPositions;
    /**
     * @default 9999
     */
    $zIndex?: CSSProperties["zIndex"];
    size?: DrawerSizes;
};
export type DrawerProps = BaseModalProps & Pick<DrawerStylesProps, "$zIndex" | "size"> & {
    dataQA: string;
    /**
     * @description hide the close button
     * @default false
     */
    hideCloseButton?: boolean;
};
