import React from "react";
import ReactModal from "react-modal";
import { IconName } from "../Icon/Icon";
import { TypographyProps } from "../Typography/Typography";
export type ModalSizes = "small" | "medium" | "large" | "extra_large" | "fullScreen";
export type ModalVariants = "destructive" | "scrollable" | "graphic" | "graphic_large" | "basic";
export interface StyledModalProps {
    /** @default basic */
    variant?: ModalVariants;
    /** @default small */
    size?: ModalSizes;
}
export interface BaseModalProps extends ReactModal.Props {
    dataQA: string;
    children?: React.ReactNode;
    /** @default true */
    preventBackgroundScroll?: boolean;
    /**
    * @description String indicating how the content container should be announced to screenreaders.
    */
    contentLabel: string;
}
export type ModalProps = BaseModalProps & StyledModalProps;
export type ModalBackdropProps = Omit<ModalProps, "children" | "container">;
export interface ModalHeadingProps extends Pick<TypographyProps, "color"> {
    children: React.ReactNode;
    /** @default h1 */
    component?: "h1" | "h2" | "h3" | "h4" | "h5" | "h6";
    dataQA: string;
    headingIcon?: IconName;
}
export interface ModalFooterProps {
    primaryButtonText?: string;
    primaryButtonOnClick?: React.MouseEventHandler<HTMLButtonElement>;
    hidePrimaryButton?: boolean;
    secondaryButtonText?: string;
    secondaryButtonOnClick?: React.MouseEventHandler<HTMLButtonElement>;
    hideSecondaryButton?: boolean;
    dataQA: string;
    className?: string;
}
