import { ProgressHTMLAttributes } from "react";
import { Margins } from "../styleguide";
export type ProgressBarState = "default" | "complete" | "error";
export type StyledProgressBarProps = ProgressHTMLAttributes<HTMLProgressElement> & Margins & {
    state: ProgressBarState;
    valueBarColor?: string;
    valueBarPattern?: string;
    backgroundColor?: string;
};
export type ProgressBarProps = Omit<StyledProgressBarProps, "state"> & {
    errorText?: string;
    value: number;
    dataQA: string;
    ariaLabel: string;
    hasError?: boolean;
    hideValue?: boolean;
    showCompleteState?: boolean;
};
